@charset "utf-8";
*{margin:0;padding:0;}
a{text-decoration:none;}
img{border:none;}
ol,ul{list-style-type:none;}
body{font-family:'Microsoft YaHei';background-color:#fff;}
.clear{clear:both;}
/***************课件框架垂直居中*********************/
/*.contain{margin:0 auto;background:url(../imgs/content_bg.png) no-repeat center top;width:1083px;height:698px;position:absolute;top:50%;left:50%;margin-left:-541.5px;margin-top:-349px;}*/
/*****************课件框架不垂直居中***********************/
.contain{margin:0 auto;background:rgba(45,45,45,0.8);position:relative;}
.content_box{width:1200px;margin:0 auto;overflow:hidden;}
.banner{padding-top:39px;margin:0 auto;width: 970px;}
.main{background:#0e0e0e;margin:40px 0;padding-bottom:40px;}
.c_left{float:left;width:800px;margin:10px 0 0 25px;position:relative;}
.right{float:left;width:330px;margin:10px 0 0 20px;}
.c_left .video_box{margin-top:44px;position:relative;margin-left:2px;}
.c_left .copyright{font-family:"楷体";font-size:18px;color:#fff;  text-align: center;  line-height: 64px;}
.c_left .copyright .have{font-size:14px;}
.c_left .copyright .copyright_txt{float:left;margin-left:90px;}
#course_player{width:800px;height:450px;}
.c_left .clarity{margin-top: 20px;margin-left: 26px;width:60px;height:78px;color:#fff;text-align:center;float:left;font-family:"Microsoft YaHei";font-size:14px;position:relative;}
.c_left .clarity ul{width:100%;float:right;display:none;position:absolute;bottom:80px;z-index:1000;}
.c_left .clarity .choose{line-height:26px;cursor:pointer;width:100%;background-color:#000;filter:alpha(opacity=90);background-color:rgba(0,0,0,0.9);color:#fff;display:block;}
.c_left .clarity .choose:hover{background-color:#2881e9;}
.c_left .clarity li{line-height:30px;cursor:pointer;background-color:#000;filter:alpha(opacity=80);background-color:rgba(0,0,0,0.8);}
.c_left .clarity li.active,.left .clarity li:hover{color:#2881e9;}
.c_left .clarity .choose img{position:relative;bottom:3px;margin-right:4px;}
.menu_title{margin:5px auto 0;width:330px;height:40px;font-family:"SimHei";font-size:18px;border-bottom:2px solid #f2f2f2;}
.menu_title .tab{display:none;}
.menu_title li{float:left;width:156px;cursor:pointer;text-align:center;height:40px;line-height:40px;letter-spacing: 4px;}
.menu_title li.active{border-bottom:2px solid #cc0000;color:#fff;}
.menu{width:330px;height:450px;margin:0 auto;padding-top:20px;}
.list .first_list{font-family:"SimSun";font-weight:bold;font-size:16px;padding:0 20px;}
.list .first_list .first_title{float:left;}
.list{height: 420px;overflow-y: scroll;color:#f2f2f2;}


/*滚动条样式  */
.list::-webkit-scrollbar{
  width:10px;
  height:10px;
  /**/
}
.list::-webkit-scrollbar-track{
  background: rgb(239, 239, 239);
  border-radius:2px;
}
.list::-webkit-scrollbar-thumb{
  background: #bfbfbf;
  border-radius:10px;
}
.list::-webkit-scrollbar-thumb:hover{
  background: #999;
}
.list::-webkit-scrollbar-corner{
  background: #179a16;
}



.switch{display:none;}
.list li{line-height:34px;}
.list li b{display:inline-block;float:left;width:15px;height:15px;background:url(../imgs/chapter.png) no-repeat;margin:9px 10px 0 20px;}
.list .first_list li.current{color:#cc0000;}
.list .first_list li.current b{background:url(../imgs/chapter_active.png) no-repeat;}
.list .second_list{font-family:"Microsoft YaHei";font-weight:normal;font-size:16px;}
.list .second_list li{line-height:30px;cursor:pointer;padding:0 10px 0 20px;}
.list .second_list li img{position: relative;bottom: 4px;  margin: 0 7px;}
.list .second_list li.current{color:#cc0000;font-size:16px;}
/**********简介************/
.list li.teacher_p b{display:inline-block;width:15px;height:15px;background:url(../imgs/teacher.png) no-repeat;margin:9px 10px 0 20px;}
.list li.teacher_p{font-family:"SimSun";font-weight:bold;font-size:16px;  }
.list li.teacher_p dl{font-family:"Microsoft YaHei";font-weight:normal;}
.list li.teacher_p dt{font-size:16px;padding-left:45px;}
.list li.teacher_p dd,.list li.course_p p{font-size:16px;padding-left:18px;width: 220px; margin: 10px auto;line-height:24px;text-indent:2em;}
.list li.course_p b{display:inline-block;width:15px;height:15px;background:url(../imgs/course.png) no-repeat;margin:9px 10px 0 20px;}
/*************意见反馈**************/
.feedback{float:right;margin-top: 14px;line-height:60px;}
/***********讲稿下载******************/
.link_btn{float:right;width:96px;margin-right:10px;}
.draft{float:left;margin:14px 16px 0 0;cursor:pointer;display:none;}



#mask{width:100%;height:100%;position:fixed;left:0;top:0;background:#000;opacity:0.5;color:#666;z-index:999;}
.continue{box-sizing: content-box;height:70px;font-size:16px;position:fixed;top:30%;left:50%;margin-left:-150px;margin-top:0;text-align:center;padding:20px 0;z-index:9999;width:300px;border-radius:16px;background-color:#fff;}
.user_choise{cursor:pointer;line-height:44px;position:absolute;left:0;bottom:0;width:100%;border-top:1px solid #ccc;border-radius:2px;}
.user_choise>span{display:inline-block;width:100%;}
